  render(){

    const {posts, counter} = this.state

      return (
        <section className="container">
          <div className="posts">
            <h1>{counter}</h1>
                {posts.map(post => (
                  <div className="post-content">
                    <div key={post.id} className="post-card">
                      <h1>{post.title}</h1>
                      <p>{post.body}</p>
                    </div>
                  </div>
                ))}
          </div>
        </section>
      )
    }
}

export default App;
