  state = {
    posts: [
      {
        id: 1,
        title: 'Titulo 1',
        body: 'Corpo 1'
      },
      {
        id: 2,
        title: 'Titulo 2',
        body: 'Corpo 2'
      },
      {
          id: 3,
          title: 'Titulo 3',
          body: 'Corpo 3'
      }
    ]
  }

  handlePClick = () => {
    this.setState({ name:'Marcos' })
  }

  handleAClick = (event) => {
    event.preventDefault()
    const {counter} = this.state
    this.setState({counter: counter + 1})
  }

  render(){

    const {name, counter} = this.state

  return (
    <div className="App">
        <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p onClick={this.handlePClick}>
          {name} {counter}
        </p>
        <a onClick={this.handleAClick} className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">
