import { Component } from 'react';
import './App.css';


class App extends Component {
  state = {
    counter: 0,
    posts: [
      {
      id: 1,
      title: 'Titulo 1',
      body: 'Corpo 1'
    },
    {
      id: 2,
      title: 'Titulo 2',
      body: 'Corpo 2'
    },
    {
        id: 3,
        title: 'Titulo 3',
        body: 'Corpo 3'
    },
  ]
  }
  timeoutUpdate = null

  componentWillUnmount(){
    clearTimeout(this.handleTimeout)
  }

  componentDidMount(){
    this.handleTimeout()
  }

  componentDidUpdate(prevState){
    this.handleTimeout()
  }


  handleTimeout = () => {
    const {posts, counter} = this.state
    posts[0].title = 'O título mudou'

    this.timeoutUpdate = setTimeout(() => {
      this.setState({posts, counter: counter + 1})
    }, 1000)
  }

  handlePClick = () => {
    this.setState({ name:'Marcos' })
  }

  handleAClick = (event) => {
    event.preventDefault()
    const {counter} = this.state
    this.setState({counter: counter + 1})
  }

  render(){

    const {posts, counter} = this.state

    return (
      <div className="App">
      <h1>{counter}</h1>
          {posts.map(post => (
            <div key={post.id}>
              <h1>{post.title}</h1>
              <p>{post.body}</p>
            </div>
          ))}
      </div>
    )
}
}

export default App;
